declare type Article = {
  source: {
    id: string;
    name: string;
  };
  author: string;
  title: string;
  description: string;
  url: string;
  urlToImage: string;
  publishedAt: string;
  content: string;
};

declare type ArticlesResponse = {
  articles: Article[];
  status: string;
  totalResults: number;
};
